mapper.select:
SELECT
        notice_id,
        user_id,
        read_status,
        read_tiem
FROM
    sys_notice_status


        AND id = #{queryParams.id}


        AND notice_id = #{queryParams.noticeId}


        AND user_id = #{queryParams.userId}


        AND read_status = #{queryParams.readStatus}


        AND read_tiem = #{queryParams.readTiem}
